// Preferred approach
<script setup lang='ts'>
interface Props {
  title: string
  items: Array<any>
  variant?: 'default' | 'compact'
}

const props = withDefaults(defineProps<Props>(), {
  variant: 'default'
})

const emit = defineEmits<{
  select: [item: any]
  close: []
}>()

const isCompact = computed(() => props.variant === 'compact')
</script>
